                  <div style={{ marginLeft: 10 }}>
                    <h3>{<Skeleton width={150} />}</h3>
                    <small>{<Skeleton width={100} />}</small>
                  </div>
                </div>
                <p>{<Skeleton count={3} />}</p>
                <div className="date">
                  <span>{<Skeleton width={"25%"} />}</span>
                </div>
              </div>
            </SkeletonTheme>
            <SkeletonTheme
              baseColor={SkBgColor}
              highlightColor={SkhighlightColor}
            >
              <div className="testimonial-card">
                <div className="intro">
                  <Skeleton width={40} borderRadius={10} height={40} />
                  <div style={{ marginLeft: 10 }}>
                    <h3>{<Skeleton width={150} />}</h3>
                    <small>{<Skeleton width={100} />}</small>
                  </div>
                </div>
                <p>{<Skeleton count={3} />}</p>
                <div className="date">
                  <span>{<Skeleton width={"25%"} />}</span>
                </div>
              </div>
            </SkeletonTheme>
            <SkeletonTheme
              baseColor={SkBgColor}
              highlightColor={SkhighlightColor}
            >
              <div className="testimonial-card">
                <div className="intro">
                  <Skeleton width={40} borderRadius={10} height={40} />
                  <div style={{ marginLeft: 10 }}>
                    <h3>{<Skeleton width={150} />}</h3>
                    <small>{<Skeleton width={100} />}</small>
                  </div>
                </div>
                <p>{<Skeleton count={3} />}</p>
                <div className="date">
                  <span>{<Skeleton width={"25%"} />}</span>
                </div>
              </div>
            </SkeletonTheme>
