import React, { useLayoutEffect } from "react";

export default function AdminBanner() {
  const [TeamBnr, setTeamBnr] = useState("");
  const SkBgColor = getComputedStyle(document.body).getPropertyValue(
    "--skeleten-base-clr"
  );
  const SkhighlightColor = getComputedStyle(document.body).getPropertyValue(
    "--skeleten-highlight-clr"
  );
  const [IsLoading, setIsLoading] = useState(false);

  useLayoutEffect(() => {
    FetchTeamImg();
  }, []);
  async function FetchTeamImg() {
    setIsLoading(true);
    await fetch("http://localhost:8000/api/fetch-team", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((Response) => {
        return Response.json();
      })
      .then((Data) => {
        setIsLoading(false);
        setTeamBnr(Data);
      })
      .catch((e) => {
        console.log(e);
      });
  }
  return (
    <>
      <div className="main-admin-intro-banner">
        <div className="m-a-i-b-wrapper">
          <div className="admin-cover-grid">
            <div className="admin-cover"></div>
            <div className="admin-cover"></div>
            <div className="admin-cover"></div>
            <div className="admin-cover"></div>
            <div className="admin-cover"></div>
          </div>
          <div className="m-a-i-b-para">Over 300+ Developers are Contributer's achieved</div>
          <div className="m-a-i-b-text">The best Professionate Website Developers & Designers are here to fulfill your recommendation.</div>
        </div>
      </div>
    </>
  );
}
