import React from 'react'

export default function Testimonials() {
    const slider = document.querySelector('.slider');
const sliderContainer = document.querySelector('.slider-container');
const sliderCards = document.querySelectorAll('.slider-card');
const sliderPrev = document.querySelector('.slider-prev');
const sliderNext = document.querySelector('.slider-next');
let sliderIndex = 0;
let sliderWidth = slider.offsetWidth;

window.addEventListener('resize', () => {
  sliderWidth = slider.offsetWidth;
  slideToCurrent(sliderIndex);
});

function slideToCurrent(index) {
  sliderContainer.style.transform = `translateX(-${sliderWidth * index}px)`;
}

function slideNext() {
  if (sliderIndex === sliderCards.length - 1) {
    sliderIndex = 0;
  } else {
    sliderIndex++;
  }
  slideToCurrent(sliderIndex);
}

function slidePrev() {
  if (sliderIndex === 0) {
    sliderIndex = sliderCards.length - 1;
  } else {
    sliderIndex--;
  }
  slideToCurrent(sliderIndex);
}

  return (
    <>
     <div class="slider">
  <div class="slider-container">
    <div class="slider-card">
      <img src="https://via.placeholder.com/150x150" alt="testimonials 1"/>
      <h3>John Doe</h3>
      <p>"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."</p>
    </div>
    <div class="slider-card">
      <img src="https://via.placeholder.com/150x150" alt="testimonials 2"/>
      <h3>Jane Doe</h3>
      <p>"Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat."</p>
    </div>
    <div class="slider-card">
      <img src="https://via.placeholder.com/150x150" alt="testimonials 3"/>
      <h3>Bob Smith</h3>
      <p>"Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur."</p>
    </div>
  </div>
  <div class="slider-nav">
    <button class="slider-prev"><i class="fa fa-chevron-left"></i></button>
    <button class="slider-next"><i class="fa fa-chevron-right"></i></button>
  </div>
</div>
 
    </>
  )
}
