import React, { useState } from "react";
import PortionEnd from "./PortionEnd";
import TestimonialCard from "./TestimonialCard";
import Skeleton, { SkeletonTheme } from "react-loading-skeleton";
export default function Testimonials() {
  const SkBgColor = getComputedStyle(document.body).getPropertyValue('--skeleten-base-clr');
    const SkhighlightColor = getComputedStyle(document.body).getPropertyValue('--skeleten-highlight-clr');
    
    const [IsLoading, setIsLoading] = useState(false);
  return (
    <>
      <div className="testimonial">
        <div className="testimonial-heading">
          <h1>Testmonials</h1>
          <p>
            "Cover what others are saying about us, Read our blowing
            testimonials"
          </p>
        </div>
        <div className="testimonial-container">
          {IsLoading ? (
            <SkeletonTheme
              baseColor={SkBgColor}
              highlightColor={SkhighlightColor}
            >
               <div className="testimonial-card">
    <div className="intro">
    <Skeleton width={50} borderRadius={10} height={50} />
      <div>
                <h3>{<Skeleton width={"80%"} />}</h3>
                {<Skeleton width={"60%"} />}
      </div>
    </div>
     <p>{<Skeleton count={2} />}</p>
    <div className="date">
      <span>23 April, 2023 8:13 AM</span>
    </div>
  </div> 
              {/* <div className="card">
                <div className={`cat-icon`}>
                  <Skeleton width={50} borderRadius={10} height={50} />
                </div>
                <h3 className="cat-title">{<Skeleton width={"80%"} />}</h3>
                <small className="pcat-title">
                  {<Skeleton width={"60%"} />}
                </small>
                <p className="cat-desc">{<Skeleton count={2} />}</p>
              </div> */}
            </SkeletonTheme>
          ) : (
            Category &&
            Category["sub category"].map((subCat, i) => {
              const GetPCategory = Category["parent category"].find((pcat) => {
                return pcat.parent_cat_id == subCat["parent_cat_id"];
              })["parent_cat_name"];
              return (
                <CategoryCard
                  Isload={IsLoading}
                  ParentCategory={GetPCategory}
                  key={subCat["id"]}
                  icon={subCat["category_icon"]}
                  title={subCat["category_title"]}
                  desc={subCat["category_desc"]}
                />
              );
            })
          )}
        </div>
      </div>
      <PortionEnd title="Load more Reviews" />
    </>
  );
}
