import React, { useEffect, useRef, useState } from "react";
import $ from "jquery";

export default function Navbar() {


  const [Dropdown, setDropdown] = useState(false);
  const [ProfileDropdown, setProfileDropdown] = useState(false);

  const HandleDropdown = () => {
    setDropdown(!Dropdown);
  };
  const HandleProfileDropdown = () => {
    setProfileDropdown(!ProfileDropdown);
  };

  // const [active, setActive] = useState(false);
  // const handleDropdown = () => {
  //   // setShow(!show);
  //   setActive(!active);
  // };

  $("document").ready(function () {
    // navbar dropdown button 
    // $(".dropdown-toggler").click(function () {
    //   let innerdata = $(this).data("toggle");
    //   console.log(innerdata);
    //   $("#" + innerdata).toggleClass("show");
    //   $(this).toggleClass("active");
    // });
    // navbar on collapsed
    $(".nav-trigger").click(function () {
      $(".nav-colapsed").toggleClass("open");
      $(this).children("i").toggleClass("fa-xmark");
    });

    // speak recognition
    $("#speak-btn-trigger").click(function () {
      $(".speak-recognization").toggleClass("active");
      $(".microphone-btn").toggleClass("active");
    });
    $("#speak-btn").click(function () {
      $(this).toggleClass("active");
    });

    // // navbar profile
    // $("#nav_profile_trigger").click(function () {
    //   $(".nav-profile").toggleClass("active");
    //   $(this).toggleClass("active");
    // });
    // const navProfileTrigger = document.querySelector("#nav_profile_trigger");
    // const navProfile = document.querySelector(".nav-profile");

    // navProfileTrigger.addEventListener("click", () => {
    //   navProfile.classList.toggle("active");
    //   navProfileTrigger.classList.toggle("active");
    // });





    $(document).click(function (e) {
      if (!$(e.target).closest(".setting").length) {
        $("#setting").removeClass("show");
        $(".setting a").removeClass("active");
      }
      if (!$(e.target).closest(".search-box-sm").length) {
        $(".search-sm-input").removeClass("show");
        $(".search-box-sm button i").removeClass("active");
      }
      if (!$(e.target).closest(".tab").length) {
        $(".tab").removeClass("active");
        $(".inner-tabs").removeClass("active");
      }
      // if (
      //   !$(e.target).closest(".user-account-dropdown").length &&
      //   !$(e.target).closest(".drop-down a").length
      // ) {
      //   $(".user-account-dropdown").removeClass("active");
      //   $(".drop-down a").removeClass("active");
      // }
      if (
        !$(e.target).closest(".nav-colapsed").length &&
        !$(e.target).closest(".nav-trigger").length
      ) {
        $(".nav-colapsed").removeClass("open");
        $(".nav-trigger").children("i").removeClass("fa-xmark");
      }

      // speak recognition tab closed
      if (
        !$(e.target).closest(".speak-recognization button").length &&
        !$(e.target).closest("#speak-btn-trigger").length
      ) {
        $(".speak-recognization").removeClass("active");
        $(".microphone-btn").removeClass("active");

        // speak recognition button disabled while close tab
        $("#speak-btn").removeClass("active");
      }

      // navbar profile tab closed
      // if (
      //   !$(e.target).closest(".nav_profile_toggler").length &&
      //   !$(e.target).closest(".nav-profile").length
      // ) {
      //   $(".nav-profile").removeClass("active");
      //   $(".nav_profile_toggler").removeClass("active");
      // }
    });
  });
  // const DropdownRef = useRef(null)

  useEffect(() => {
    const DropdownToggler = document.querySelector(".dropdown-toggler");
    const ProfileDropdownToggler = document.querySelector(".dropdown-toggler");
    function handleClickOutside(e) {
      if (DropdownToggler && !DropdownToggler.contains(e.target) && DropdownToggler && !DropdownToggler.contains(e.target)) {
        setDropdown(false)
      }
    }
    document.addEventListener("click", handleClickOutside);






    var getTheme = localStorage.getItem("theme_mode")
    const { body } = document;
    body.classList.add(getTheme)
    SwitchTheme();
  }, [])

  const SwitchTheme = () => {
    const themes = document.querySelectorAll(".themeModes li button");
    const { body } = document;
    themes.forEach((theme) => {
      theme.addEventListener('click', () => {
        const mode = theme.dataset.mode;
        body.classList.remove(mode === "dark" ? "light" : "dark");
        localStorage.setItem("theme_mode", mode);
        body.classList.add(mode);
      });
    });
  };
  return (
    <>

      <div className='free-space'></div>
      <nav>
        <div className="navlogo ">sssss</div>
        <div className="navigate-by-search">
          <div className="nav-center-search-box">
            <input type="text" placeholder="Find any blog that you like" />
            <div>
              <i className="fa-regular fa-magnifying-glass"></i>
            </div>
          </div>
        </div>
        <div className="nav-btns">
          <div className="nav-btn">
            <a className="nav-link ">
              <i className="fa-regular fa-bell"></i>
              <div className="indicator red"></div>
            </a>
          </div>
          <div className="nav-btn">
            <a className="nav-link">
              <i className="fa-regular fa-bookmark"></i>
            </a>
          </div>
          <div className="drop-down nav-btn">
            <a onClick={HandleDropdown} className={` dropdown-toggler nav-link ${Dropdown ? "active" : ""}`} >
              <i className="fa-regular fa-palette"></i>
            </a>
            <div className={`user-account-dropdown ${Dropdown ? "active" : ""}`}>
              <ul className="navbar_dropdown themeModes">
                <li>
                  <button data-mode="light" className="navbar_drop_links">Light Mode</button>
                </li>
                <li>
                  <button data-mode="dark" className="navbar_drop_links">Dark Mode</button>
                </li>
                <li>
                  <button data-mode="light" className="navbar_drop_links">System Default</button>
                </li>
              </ul>
            </div>
          </div>
          {/* <!-- mini profile  functionality --> */}
          <div className="nav-btn">
            <a  onClick={HandleProfileDropdown} className={`nav_profile_toggler nav-link ${ProfileDropdown ? 'active' : ''}`}>
              <i className="fa-regular fa-user"></i>
            </a>
            <div className={`nav-profile ${ProfileDropdown ? 'active' : ''}`}>
              <div className="nav-profile-top">
                <div className="profile-cover">
                  <label for="profile_cover">
                    <i className="fa-regular fa-camera"></i>
                  </label>
                  <input
                    type="file"
                    id="profile_cover"
                    style={{ visibility: "hidden" }}
                  />
                </div>
                <div>
                  <h4>Rayyan Ali Khan</h4>
                  <p>rayyanali4422@gmail.com</p>
                </div>
              </div>
              <div className="view-profile-btn-case">
                <button className="view-profile-btn">Manage your Account</button>
              </div>
              <div>
                <ul className="navbar_dropdown">
                  <li>
                    <a className="navbar_drop_links">
                      <i className="fa-regular fa-circle-info"></i> Help & Support
                    </a>
                  </li>
                  <li>
                    <a className="navbar_drop_links">
                      <i className="fa-regular fa-file-shield"></i> Privacy Policy
                    </a>
                  </li>
