import React, { useEffect } from 'react'
import DarkThemeImage from "../../images/DarkThemeIcon.png";
import LightThemeIcon from "../../images/LightThemeIcon.png";

export default function FontRow(props) {
    const getFont = localStorage.getItem("font_mode");

    useEffect(() => {
        document.body.classList.add(getFont);
        SwitchFont();

    }, [getTheme]);

    const SwitchFont = () => {
        const fonts = document.querySelectorAll(".appearance_container font_container .custom_box");
        themes.forEach((theme) => {

            theme.addEventListener('click', (e) => {
                const mode = e.currentTarget.dataset.mode;
                document.body.classList.remove(mode === "dark" ? "light" : "dark");
                localStorage.setItem("theme_mode", mode);
                document.body.classList.add(mode);
                if (mode === "dark") {
                    props.fontProps.ShowToast(
                        "fa-moon-cloud gray",
                        "Dark Mode Enabled",
                        "The dark mode has been successfully turned on."
                    )
                } else if (mode === "light") {
                    props.fontProps.ShowToast(
                        "fa-sun orange",
                        "Light Mode Enabled",
                        "The light mode has been successfully turned on."
                    )

                }
                updateTheme();
            })
        })
    }

    const updateTheme = () => {
        const themes = document.querySelectorAll(".appearance_container font_container .custom_box");
        const currentTheme = localStorage.getItem("theme_mode");
        themes.forEach((theme) => {
            const mode = theme.dataset.mode;
            if (mode === currentTheme) {
                theme.classList.add("active");
            } else {
                theme.classList.remove("active");
            }
        });
    }
  return (
<>
<p className='one_line_para'>Customize font style</p>
            <div className='appearance_container'>
                <div>
                    <button data-mode="roboto" className={`custom_box ${getTheme === "roboto" ? "active" : ""}`}>
                        <img src={DarkThemeImage} alt="" />
                        <div className='select_custom_switch'>
                            <label htmlFor='select_theme1'><i className='fa-solid fa-check'></i></label>
                            <input id='select_theme1' type="radio" />
                        </div>
                    </button>
                    <div className='custom_box_title'>Roboto <span>sans serif</span></div>
                </div>
                <div>
                    <button data-mode="sansita" className={`custom_box ${getTheme === "sansita" ? "active" : ""}`}>
                        <img src={LightThemeIcon} alt="" />
                        <div className='select_custom_switch'>
                            <label htmlFor='select_theme2'><i className='fa-solid fa-check'></i></label>
                            <input id='select_theme2' type="radio" />
                        </div>
                    </button>
                    <div className='custom_box_title'>Sansita <span>sans serif</span></div>
                </div>
            </div>
</>
  )
}
