import React, { useState, useEffect } from 'react';
import DarkThemeIcon from '../../images/DarkThemeIcon.png';
import LightThemeIcon from '../../images/LightThemeIcon.png';

const THEME_STORAGE_KEY = 'theme_mode';

function ThemeRow({ themeProps }) {
  const [themeMode, setThemeMode] = useState(localStorage.getItem(THEME_STORAGE_KEY) || 'light');

  useEffect(() => {
    document.body.classList.add(themeMode);
  }, [themeMode]);

  function switchTheme(newMode) {
    document.body.classList.remove('dark', 'light');
    document.body.classList.add(newMode);
    localStorage.setItem(THEME_STORAGE_KEY, newMode);

    const toastProps =
      newMode === 'dark'
        ? {
            icon: 'fa-moon-cloud igray',
            title: 'Dark Mode Enabled',
            message: 'The dark mode has been successfully turned on.',
          }
        : {
            icon: 'fa-sun iorange',
            title: 'Light Mode Enabled',
            message: 'The light mode has been successfully turned on.',
          };

    themeProps.ShowToast(toastProps);
    setThemeMode(newMode);
  }

  return (
    <>
      <p className="one_line_para">Tailor this website to your preferences by adding your personal touch.</p>
      <div className="appearance_container theme_container">
        <div>
          <button
            className={`custom_box ${themeMode === 'dark' ? 'active' : ''}`}
            onClick={() => switchTheme('dark')}
          >
            <img src={DarkThemeIcon} alt="" />
            <div className="select_custom_switch">
              <label htmlFor="select_theme1">
                <i className="fa-solid fa-check"></i>
              </label>
              <input id="select_theme1" type="radio" />
            </div>
          </button>
          <div className="custom_box_title">Nightfall</div>
        </div>
        <div>
          <button
            className={`custom_box ${themeMode === 'light' ? 'active' : ''}`}
            onClick={() => switchTheme('light')}
          >
            <img src={LightThemeIcon} alt="" />
            <div className="select_custom_switch">
              <label htmlFor="select_theme2">
                <i className="fa-solid fa-check"></i>
              </label>
              <input id="select_theme2" type="radio" />
            </div>
          </button>
          <div className="custom_box_title">Sunrise</div>
        </div>
      </div>
    </>
  );
}

export default ThemeRow;
