import React, { useEffect, useLayoutEffect, useState } from "react";
import BlogCard from "./BlogCard";
import PortionEnd from "../Assets/PortionEnd";
import PortionTop from "../Assets/PortionTop";
import Skeleton, { SkeletonTheme } from "react-loading-skeleton";

export default function BlogContainer() {
  const [Posts, setPosts] = useState("");
  const SkBgColor = getComputedStyle(document.body).getPropertyValue(
    "--skeleten-base-clr"
  );
  const SkhighlightColor = getComputedStyle(document.body).getPropertyValue(
    "--skeleten-highlight-clr"
  );

  const [IsLoading, setIsLoading] = useState(false);
  useLayoutEffect(() => {
    FetchPosts();
  }, []);
  async function FetchPosts() {
    setIsLoading(true);
    await fetch("http://localhost:8000/api/fetch-posts", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((Response) => {
        return Response.json();
      })
      .then((Data) => {
        setIsLoading(false);
        setPosts(Data);
        console.log(Data)
      })
      .catch((e) => {
        console.warn(e);
      });
  }
  return (
    <>
      <div className="cards-container">
        <PortionTop title={"Latest Articles"} btn_title={"browse all articles"} btn_url={"/browse"} />
        <div className="post-grid">

        {IsLoading ? [...Array(8)].map((_, i) => (
                <SkeletonTheme
                  key={i}
                  baseColor={SkBgColor}
                  highlightColor={SkhighlightColor}
                >
                  <div className="card">
                    <div className={`cat-icon`}>
                      <Skeleton width={50} borderRadius={10} height={50} />
                    </div>
                    <h3 className="cat-title">{<Skeleton width={"80%"} />}</h3>
                    <small className="pcat-title">
                      {<Skeleton width={"60%"} />}
                    </small>
                    <p className="cat-desc">{<Skeleton count={2} />}</p>
                  </div>
                </SkeletonTheme>
              )) : (
              Posts &&
              Posts.map((post, i) => {
                if (i < 8) {
         return <BlogCard data={post} key={i}/>
                }
              })
            )}

        </div>
        <PortionEnd title="Load More Articles"/>
      </div>
    </>
  );
}
