import React, { useState } from 'react'
// stripe
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
import CheckoutForm from "./Components/PaymentGatway/CheckoutForm";

const stripePromise = loadStripe("pk_test_51NArHPDqLoKHNkLbeZepmk7Vx6XiF0tforDXSdzXJn8PKmNvOp5vUMDecIwSNVEpFKd4SSyrOfxlqkccsgRqgp5600SrJ9NF0g");
export default function Subscription() {
    const [clientSecret, setClientSecret] = useState("");

    useEffect(() => {
        const items = { "price":100 }
            // Create PaymentIntent as soon as the page loads
            try{
        
              fetch("http://localhost:8000/api/create-payment-intent", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(items),
              })
              .then((res) => {
                
                return  res.json();
                
              })
              .then((data) => {
                setClientSecret(data.clientSecret)
              })
            } catch(error){
        console.log(error)
            }
        
        
        
          }, []);

  const appearance = {
    theme: 'night',
  };
  const options = {
    clientSecret,
    appearance,
  };

  return clientSecret && (
    <Elements options={options} stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
 )
}
