import React from "react";
import $ from "jquery";

export default function AdminBanner() {
  $(document).ready(() => {
    let admins = $(".admin-cover-grid .admin-cover");
    let centeradmin = Math.floor(admins.length / 2);

    // admins.forEach(admin => {
    // if(admins[admin] === centeradmin){
    //       admins[admin].addClass("active")
    //     }
    // });
    for (let i = 0; i <= admins.length; i++) {
      if (i == centeradmin) {
        admins[i].classList.add("center");
        // console.log(admins[i]);
      }else if(i == (centeradmin - 1) && i == (centeradmin + 1)){
        admins[i].classList.add("center-step-down");
      
      }else if(i == (centeradmin - 2) && i == (centeradmin + 2)){
        admins[i].classList.add("center-step-down-2");
      }
      // else if(i == (centeradmin - 2) || i == (centeradmin + 2)){
      //   admins[i].classList.add("center-step-down");
      // }
      // console.log(admins[i])
    }
    // admins.each((admin) => {
    // console.log(admins[admin].indexof())
  });
  // });
  return (
    <>
      <div className="main-admin-intro-banner">
        <div className="m-a-i-b-wrapper">
          <div className="admin-cover-grid">
            <div className="admin-cover 1"></div>
            <div className="admin-cover 2"></div>
            <div className="admin-cover 3"></div>
            
          </div>
          <div className="m-a-i-b-text">
            The best Professionate Website Developers & Designers are here for
            you
          </div>
        </div>
      </div>
    </>
  );
}
