import React, { useRef, useState } from "react";
import { useParams } from "react-router-dom";
import plane from "../../images/plane.png";
export default function ResetPassword(props) {
  const [ShowRePass, setShowRePass] = useState(false);
  const { token } = useParams();
  const ResetPassFieldRef = useRef(null);
  const RePassFieldRef = useRef(null);
  const ResetPassRef = useRef(null);
  const NewPassErrRef = useRef(null);
  const RePassErrRef = useRef(null);

  async function HandleResetPass(e) {
    e.preventDefault();
    // VerifyEmailForPassRef.current.innerHTML = "<i class='fa-light fa-sync fa-spin'></i>"
    // await fetch(`http://localhost:8000/api/reset-password`, {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //   },
    //   body: JSON.stringify({ email: ResetPassFieldRef.current.value }),
    // })
    //   .then((Response) => {
    //     return Response.json()
    // }).then((Res)=>{

    //   console.log(Res);
    //   VerifyEmailForPassRef.current.innerHTML = "Reset Link Sended <i class='fa-solid fa-check-circle'></i>"

    //   props.ShowToast(
    //         "fa-check-circle igreen",
    //         "Password reset link sended on your email address"
    //       );

    //   })
    //   .catch((e)=>{
    //     console.log(e)
    //   })
  }
  function PasswordToggler() {
    setShowPass(!ShowPass);
  }
  function RePasswordToggler() {
    setShowRePass(!ShowPass);
  }
  return (
    <div className="reset-pass-wrapper">
      <div className="reset-pass-container">
        <div className="reset-pass-content">
          <div className="img">
            <img src={plane} loading="lazy" alt="" />
          </div>
          <h2 className="">Reset a New Password</h2>
          <p>
            Please enter your email address below to recieve your user and a new
            password.
          </p>
        </div>
        <form method="post" onSubmit={HandleResetPass} className="input-box">
          <div>
            <label htmlFor="new_pass">New Password</label>
            <input
              ref={ResetPassFieldRef}
              type="text"
              id="new_pass"
              name="new_pass"
              required
            />
            <p ref={NewPassErrRef}></p>
                     <span
                  onClick={PasswordToggler}
                  className={`${ShowNewPass ? "" : "hidepass"} showpass`}
                >
                  <i
                    className={`fa-solid fa-eye${ShowNewPass ? "" : "-slash"} `}
                  ></i>
                </span>
          </div>
          <div>
            <label htmlFor="new_re_pass">Repeat Password</label>
            <input
              ref={RePassFieldRef}
              type="text"
              id="new_re_pass"
              name="new_re_pass"
              required
            />
            <p ref={RePassErrRef}></p>
                     <span
                  onClick={RePasswordToggler}
                  className={`${ShowRePass ? "" : "hidepass"} showpass`}
                >
                  <i
                    className={`fa-solid fa-eye${ShowRePass ? "" : "-slash"} `}
                  ></i>
                </span>
          </div>
          <div className="submit-btn">
            <button type="submit" ref={ResetPassRef}>
              Reset Password
            </button>
          </div>
        </form>
      </div>
    </div>
    // <div>{token}</div>
  );
}
