import React, { useRef, useState } from "react";

export default function Review(props) {
  const [image, setImage] = useState(null);

  const RevNameRef = useRef(null);
  const RevOccRef = useRef(null);
  const RevEmailRef = useRef(null);
  const RevMsgRef = useRef(null);
  const RevImgRef = useRef(null);
  const RevSubmitBtnBoxRef = useRef(null);
  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };
  async function HandleReview(e) {
    e.preventDefault();
    if (localStorage.getItem("token") == undefined) {
      props.ShowToast(
        "fa-xmark-circle ired",
        "Login Reguired",
        "Login your account, Now you can write a reviews or testemonials"
      );
    } else {
      if (
        RevNameRef.current.value != "" &&
        RevEmailRef.current.value != "" &&
        RevMsgRef.current.value != ""
      ) {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        let CheckEmail = emailRegex.test(RevEmailRef.current.value);
        if (RevNameRef.current.length <= 100) {
          props.ShowToast("fa-xmark-circle ired", "Maximum 100 Words Required");
        } else if (!CheckEmail) {
          props.ShowToast("fa-xmark-circle ired", "Invalid Email Address");
        } else if (RevMsgRef.current.value <= 250) {
          props.ShowToast("fa-xmark-circle ired", "Maximum 250 Words Required");
        } else {
const ReviewData = new FormData();
ReviewData.append("name",RevNameRef.current.value)
ReviewData.append("email",RevEmailRef.current.value)
ReviewData.append("occupation",RevOccRef.current.value)
ReviewData.append("msg",RevMsgRef.current.value)
ReviewData.append("image",image)
          // const ReviewData = {
          //   name: RevNameRef.current.value,
          //   email: RevEmailRef.current.value,
          //   occupation: RevOccRef.current.value,
          //   msg: RevMsgRef.current.value,
          //   image: image,
          // };
          // console.log(image)
          await fetch("http://localhost:8000/api/review", {
            method: "POST",
            headers: {
              'Content-Type':'application/json',
              // 'Content-Type': 'multipart/form-data; boundary=----WebKitFormBoundary7MA4YWxkTrZu0gW',
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
            
            body: ReviewData,
          })
            .then((Response) => {
              return Response.json();
            })
            .then((data) => {
              console.log(data);
            })
            .catch((e) => {
              console.log(e);
            });
        }
      } else {
        props.ShowToast(
          "fa-xmark-circle ired",
          "Empty Fields",
          "Anyone field a empty plase filled it first."
        );
      }
    }
  }
  return (
    <div
      style={{ display: props.IsActive ? "flex" : "none" }}
      className="contact-box review-box"
    >
      <div className="review-contact-head">
        <h1>Write a Review</h1>
        <p>
          Lorem, ipsum dolor sit amet consectetur adipisicing elit. Maiores,
          iusto?
        </p>
      </div>
      <form method="post" onSubmit={HandleReview}>
        <div className="input-grid">
          <div className="input-box">
            <label htmlFor="name">Full Name</label>
            <input type="text" ref={RevNameRef} id="name" />
          </div>
          <div className="input-box">
            <label htmlFor="occupation">
              Occupation <small>(Optional)</small>
            </label>
            <input type="text" ref={RevOccRef} id="occupation" />
          </div>
        </div>
        <div className="input-box">
          <label htmlFor="email">Email Address</label>
          <input type="email" ref={RevEmailRef} id="email" />
        </div>
        <div className="input-box">
          <label htmlFor="review">Write a Review</label>
          <textarea
            name=""
            id="review"
            ref={RevMsgRef}
            cols="30"
            rows="5"
          ></textarea>
        </div>
        <div className="input-button">
        <input type="hidden" name="boundary" value="----WebKitFormBoundary7MA4YWxkTrZu0gW" />
          <input
            onChange={handleImageChange}
            type="file"
            ref={RevImgRef}
            id="image"
            style={{ display: "none" }}
          />
          <label htmlFor="image" className="reviewer-image">
            <i className="fa-regular fa-camera"></i>
          </label>
          <button ref={RevSubmitBtnBoxRef}>Send Review</button>
        </div>
      </form>
    </div>
  );
}
