import React,{useState,useEffect} from "react";
import { Skeleton } from 'antd';
export default function BlogCard() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState(null);
  useEffect(() => {
    // Simulate an API call to fetch data
    setTimeout(() => {
      setData({ /* Some data */ });
      setLoading(false);
    }, 2000);
  }, []);
  return (
    <>

    <Skeleton  avatar={true} title={true}/>
    <div className="card">
      <div className="hover-btns">
        <button>
          <i className="fa-light fa-house"></i>
        </button>
        <button>
          <i className="fa-light fa-heart"></i>
        </button>
      </div>
      <div className="card-img">
        <img src="https://source.unsplash.com/random/?iphone14pro" alt="" />
        <div className="pro-access-tag">
          {/* <div className="admin-image">
            
          </div> */}
          <i className="fa-solid fa-gem"></i>
          <span>Pro Essential</span>
        </div>
      </div>
      <div className="card-content">
        <h3>Macbook Pro M1.</h3>
        <p>Lorem ipsum dolor sit amet.</p>
        <div className="tags">
          <span>Html</span>
          <span>css</span>
          <span>Js</span>
          <span>JQuery</span>
          <span>Php</span>
          <span>Mysql</span>
        </div>
        <div className="card-btns">
          <div>
            <span>25 Feburary 2023</span>
          </div>
          <div>
            <button>Preivew</button>
          </div>
        </div>
      </div>
    </div>
    </>
  );
}
