import React from "react";

import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css'

export default function CategoryCard(props) {
const Icon =  props.icon.split(",");
  return (
    <>
    <div className="card">
      <div style={{background:Icon[0]}} className={`cat-icon`}>
        <i style={{color:Icon[2]}} className={Icon[1] || <Skeleton/>}></i>
      </div>
      <h3 className="cat-title">{props.title || <Skeleton/>}</h3>
      <small className="pcat-title">{props.ParentCategory || <Skeleton count={1}/>}</small>
      <p className="cat-desc">{props.desc || <Skeleton count={2}/>}</p>
      <a className="cat-btn">
        <i className="fa-solid fa-arrow-right"></i>
      </a>
    </div>
    </>
  );
}
