import React, { useRef } from 'react'
import plane from "../../images/plane.png"
export default function ResetPassword() {
    const LoginEmailRef = useRef(null);


async function HandleResetPass(e){
    e.preventDefault();
    var token = localStorage.getItem("token");

    const fetchResult = fetch(`http://localhost:8000/api/reset-password`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body:JSON.stringify(LoginEmailRef.current.value)
      })
      .then((Response)=>{
          console.log(Response)
return Response.json()
      })
    }

    return (
        <>
            <div className="reset-pass-wrapper">
                <div className="reset-pass-container">
                    <div className='reset-pass-content'>
                        <div className='img'>
                            <img src={plane} loading='lazy' alt="" />
                        </div>
                        <h2 className="">
                            Forget Your Password?
                        </h2>
                        <p>
                            Please enter your email address below to recieve your user and a new password.
                        </p>
                    </div>
                    <form method='post' onSubmit={HandleResetPass} className='input-box'>
                        <div>
                            <label htmlFor="verify_email">Verify Email Address</label>
                            <input
                            ref={LoginEmailRef}
                                type="text"
                                id="verify_email"
                                name="verify_email"
                                required
                            />
                        </div>
                        <div className="submit-btn">
                            <button type="submit" className=""
                            >Send Request</button>
                        </div>
                    </form>
                </div>
            </div>
        </>
    )
}
