import React, { useEffect, useState } from 'react'

export default function FilterDropdown(props) {
  const [FilterDropdown, setFilterDropdown] = useState(false)

  function HandleFilterDropdown() {
    setFilterDropdown(!FilterDropdown)
  }
  useEffect(() => {
    document.addEventListener("click", handleClickOutside);

  }, [])

  function handleClickOutside(e) {
    const filter_dropdown_btn = document.querySelector(".filter-dropdown-btn");
    // const ProfileDropdownToggler = document.querySelector(".nav_profile_toggler");
    if (filter_dropdown_btn && !filter_dropdown_btn.contains(e.target)) {
      setFilterDropdown(false)
    }
  }

  return (
    <div className='filter-dopdown-wrapper'>
      <button className='filter-dropdown-btn' onClick={HandleFilterDropdown}>
        <div>{props.btnTitle}</div>
        <div><i className='fa-solid fa-angle-down'></i></div>
      </button>
      <div className={`filter-dropdown-list ${FilterDropdown ? "active" : ""}`}>
        <ul>
          <li><input id='1' type="checkbox" /> <label htmlFor='1'>Javascript</label></li>
          <li><input type="checkbox" /> <label>Javascript</label></li>
          <li><input type="checkbox" /> <label>Javascript</label></li>
          <li><input type="checkbox" /> <label>Javascript</label></li>
        </ul>
      </div>
    </div>
  )
}
