import React from "react";
import CategoryCard from "./CategoryCard";
import PortionTop from "../Assets/PortionTop";

export default function CategoryContainer() {
  return (
    <>
      <div className="carousel-wrapper">
        <PortionTop
          title={"Navigate By Categories"}
          btn_title={"See all categories"}
          btn_link={"/category"}
        />
        <div className="card-carousel">
          <CategoryCard
            cat={"html"}
            icon={"fa-light fa-chart-tree-map"}
            title={"Semantic HTML 5"}
            desc={"Simple And Easy Server Side Language Card 1 Description"}
          />

          <CategoryCard
            cat={"css"}
            icon={"fa-light fa-bezier-curve"}
            title={"Styled With CSS 3"}
            desc={"Card 2 Description Simple And Easy Server Side Language"}
          />

          <CategoryCard
            cat={"js"}
            icon={"fa-light fa-chart-network"}
            title={"Logical Javascript"}
            desc={
              "Pixels, code, and everything nice, that's what JavaScript is made of."
            }
          />

          <CategoryCard
            cat={"php"}
            icon={"fa-light fa-aperture"}
            title={"HyperText Preprocessor"}
            desc={
              "Simple And Easy Server Side Language Simple And Easy Server Side Language"
            }
          />

          <CategoryCard
            cat={"mysql"}
            icon={"fa-brands fa-cloudversify"}
            title={"MySql Database"}
            desc={
              "Simple And Easy Server Side Language Simple And Easy Server Side Language"
            }
          />

          <CategoryCard
            cat={"react"}
            icon={"fa-brands fa-react"}
            title={"React Js"}
            desc={
              " Simple And Easy Server Side Language vSimple And Easy Server Side Language"
            }
          />
        </div>
      </div>
    </>
  );
}
