import './Css/style.css';
import Navbar from './Components/Assets/Navbar';
// import Header from './Components/Header';
import Main from './Components/Pages/Main';
import Footer from './Components/Assets/Footer';
import Registration from './Components/Registration/Registration';
import Toast from './Components/Assets/Toast';
import { useState } from 'react';
import Login from './Components/Registration/Login';
import Header from './Components/Assets/Header';
import ResetPassword from './Components/Registration/ResetPassword';
import BrowseBlogs from './Components/Pages/BrowseBlogs';
import BlogDetails from './Components/Pages/BlogDetails';
import Notification from './Components/Pages/Notification';
import Sidebar from './Components/Assets/Sidebar';
import Settings from './Components/Settings/Settings';
import { BrowserRouter, Route, Link } from 'react-router-dom';
function App() {
  const [activeToast, setActiveToast] = useState(null)
  const ShowToast = (icon,title, msg) => {
    setActiveToast({icon,title,msg})
    setTimeout(() => {
      setActiveToast(null)
    }, 3500);
  }

  return (
    <>
    <BrowserRouter>
    <Routes>

    <Route path="/home" component={Main} />
    </Routes>



    </BrowserRouter>



      <Navbar ShowToast={ShowToast} />
      <Header/>
      <Settings ShowToast={ShowToast}/>
      {/* <Sidebar/> */}
      {/* <BlogDetails/> */}
      {/* <BrowseBlogs/> */}
      {/* <Main /> */}
      {/* <Login/> */}
      {/* <ResetPassword/> */}
      {/* <Registration/> */}
      {/* <Notification/> */}
      <Toast toastData={activeToast} removeToast={setActiveToast}/>
      <Footer />

    </>
  );
}

export default App;
