import { useState, lazy, Suspense} from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import './Css/style.css';
import Navbar from './Components/Assets/Navbar';
import Header from './Components/Assets/Header';
import Main from './Components/Pages/Main';
import Footer from './Components/Assets/Footer';
import Registration from './Components/Registration/Registration';
import Toast from './Components/Assets/Toast';
import Login from './Components/Registration/Login';
import ResetPassword from './Components/Registration/ResetPassword';
// import BrowseBlogs from './Components/Pages/BrowseBlogs';
import BlogDetails from './Components/Pages/BlogDetails';
import Notification from './Components/Pages/Notification';
import Settings from './Components/Settings/Settings';



// import {LazyLoad} from "./Components/Assets/LazyLoad.js"


const BrowseBlogs = lazy(() => import("./Components/Pages/BrowseBlogs"))


function App() {
  const [activeToast, setActiveToast] = useState(null)
  const ShowToast = (icon, title, msg) => {
    setActiveToast({ icon, title, msg })
    setTimeout(() => {
      setActiveToast(null)
    }, 3500);
  }

  return (
    <>

        <BrowserRouter>
          <Navbar ShowToast={ShowToast} />
          <Header />
      <Suspense fallback={"loodings"}>
          <Routes>
            <Route path="/" element={<Main />} />
            <Route path="/browse" element={<BrowseBlogs />} />
            <Route path="/browse/details" element={<BlogDetails />} />
            <Route path="/notification" element={<Notification />} />
          </Routes>


          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Registration />} />
            <Route path="/login/reset" element={<ResetPassword />} />
          </Routes>


          <Routes>
            <Route path="/settings" element={<Settings ShowToast={ShowToast} />} />
            <Route path="/settings/profile" element={<Settings ShowToast={ShowToast} Active="profile" />} />
            <Route path="/settings/billing" element={<Settings ShowToast={ShowToast} Active="billing" />} />
            <Route path="/settings/theme" element={<Settings ShowToast={ShowToast} Active="theme" />} />
          </Routes>
      </Suspense>

          <Toast toastData={activeToast} removeToast={setActiveToast} />
          <Footer />
        </BrowserRouter>

      {/* <Sidebar/> */}
      {/* <BlogDetails/> */}
      {/* <BrowseBlogs/> */}
      {/* <Login/> */}
      {/* <ResetPassword/> */}
      {/* <Registration/> */}
      {/* <Notification/> */}

    </>
  );
}

export default App;
