import React from "react";
import Slider from "./Slider";
import Header from "./Header";
import $ from "jquery";
import PortionTitle from "./PortionTitle";
import CategoryContainer from "./CategoryContainer";

export default function Main() {
  $("document").ready(function () {
    const carousel = $(".carousel");
    const cardWidth = $(".card").width();
    let cardIndex = 0;

    $(".carousel-arrow").click(function () {
      const direction = $(this).hasClass("prev") ? -1 : 1;
      cardIndex += direction;
      carousel.animate({ scrollLeft: cardWidth * cardIndex }, 500);
    });
  });

  return (
    <>
      <Header />

      <Slider />
<CategoryContainer/>

   

      <div className="portion-end">
        <button className="loadmore">See all blogs</button>
      </div>
    </>
  );
}
