import React from 'react'

export default function TestimonialCard(props) {
 const  ImgLink = `http://localhost:8000${props.data.image}`; 
 const formattedDate = formatDate(props.data.created_at);
 const formatDate = (dateString) => {
  const options = {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: 'numeric',
    minute: 'numeric',
    hour12: true,
  };

  const formattedDate = new Date(dateString).toLocaleDateString(undefined, options);
  const formattedTime = new Date(dateString).toLocaleTimeString(undefined, options);

  return `${formattedDate} ${formattedTime}`;
};

  return (

    <div className="testimonial-card">
    <div className="intro">
      <img
        src={ImgLink}
        alt="testimonials 1"
      />
      <div>
        <h6>{props.data.name}</h6>
        <small>{props.data.occupation}</small>
      </div>
    </div>
    <p>
    {props.data.msg}</p>
    <div className="date">
      {/* <span>{props.data.created_at} 23 April, 2023 8:13 AM</span> */}
      <span>{formattedDate}</span>
    </div>
  </div>  )
}
