import React, { useRef, useState } from "react";

export default function Information() {
  const [ShowProPass, setShowProPass] = useState(false);
  const [ShowReProPass, setShowReProPass] = useState(false);

  const [ProPassVerify, setNewPassVerify] = useState(false);
  const [RePassVerify, setRePassVerify] = useState(false);

const ProPassFieldRef= useRef(null);
const NewProPassErrRef= useRef(null);
const ReProPassFieldRef= useRef(null);
const NewReProPassErrRef= useRef(null);
  function HandleProPassField(e) {
    let value = e.target.value;
    const passwordRegex =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    let CheckPass = passwordRegex.test(value);

    if (!CheckPass) {
      NewProPassErrRef.current.innerText =
        "At least one lowercase, one uppercase letter, one digit, one special character (@, $, !, %, *, ?, &) And Minimum length of 8 characters";
      setNewPassVerify(false);
    } else {
      NewProPassErrRef.current.innerText = null;
      setNewPassVerify(true);
    }
  }

  function HandleReProPassField(e) {
    let value = e.target.value;
    const passwordRegex =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    let CheckPass = passwordRegex.test(value);

    if (!CheckPass) {
      NewReProPassErrRef.current.innerText =
        "At least one lowercase, one uppercase letter, one digit, one special character (@, $, !, %, *, ?, &) And Minimum length of 8 characters";
      setRePassVerify(false);
    } else {
      NewReProPassErrRef.current.innerText = null;
      setRePassVerify(true);
    }
  }

  async function HandleResetPass(e) {
    e.preventDefault();
    if (NewPassVerify && RePassVerify) {
      if (ResetPassFieldRef.current.value != RePassFieldRef.current.value) {
        return props.ShowToast(
          "fa-xmark-circle ired",
          "Passwords Not Matched",
          "Password fields does not matched."
        );
      } else {
        ResetPassBtnRef.current.innerHTML =
          "<i class='fa-light fa-sync fa-spin'></i>";
        submitBtn.current.style.width = "15%";
        await fetch(
          `http://localhost:8000/api/reset/${
            localStorage.getItem("reset_token") != undefined
              ? localStorage.getItem("reset_token")
              : token
          }`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({ password: ResetPassFieldRef.current.value }),
          }
        )
          .then((Response) => {
            return Response.json();
          })
          .then((Res) => {
            console.log(Res);
            submitBtn.current.style.width = "50%";
            if (Res.success) {
              ResetPassBtnRef.current.innerHTML =
                "Password Reset <i class='fa-solid fa-check-circle'></i>";
              localStorage.removeItem("reset_token");
              props.ShowToast(
                "fa-check-circle igreen",
                "Password Successfully Reset",
                "Your password successfully reset, Now go to login with new password"
              );
            } else {
              ResetPassBtnRef.current.innerHTML = "Reset Password";
              props.ShowToast(
                "fa-xmark-circle ired",
                "Request Expired",
                Res.msg.password ?? Res.msg
              );
            }
          })
          .catch((e) => {
            submitBtn.current.style.width = "50%";

            ResetPassBtnRef.current.innerHTML = "Password Reset ";
            props.ShowToast(
              "fa-xmark-circle ired",
              "Server Error ",
              "Server sending no response."
            );
          });
      }
    } else {
      props.ShowToast(
        "fa-xmark-circle ired",
        "Invalid Password pattern",
        "Password fields are not Validate."
      );
    }
  }

  function ProPasswordToggler() {
    setShowProPass(!ShowProPass);
  }
  function ReProPasswordToggler() {
    setShowReProPass(!ShowReProPass);
  }
  return (
    <div className="personal_pro_wrap">
      <div className="personal_pro_head">Personal Information</div>
      <div className="personal_pro_container">
        <div className="personal_pro_fields">
          {/* <form className="">
            <div className="personal_pro_h2">Account Details</div>

            <div className="input-box">
              <label for="set-pro_name">Full Name</label>
              <input type="text" id="set-pro_name" />
              <p>You can change your full name easily. </p>
            </div>
            <div className="input-box">
              <label for="set-pro_email">Email Address</label>
              <input type="email" id="set-pro_email" />
              <p>
                You can't change your email address. Because it is unique one.{" "}
              </p>
            </div>

            <div class="input-button">
              <button>Update Changes</button>
            </div>
          </form> */}

          <form onSubmit={HandleChangepass}>
            <div className="personal_pro_h2">Change Password</div>
            <p>
              if you want to change your password? then you can change it by
              filling these new password fields below.
            </p>

            <div class="input-box">
              <label for="set_pro_pass">Password</label>
              <input
                ref={ProPassFieldRef}
                onChange={HandleProPassField}
                type={ShowProPass ? "text" : "password"}
                id="set_pro_pass"
              />
              <p ref={NewProPassErrRef}>
                Change the password but create a strong one.
              </p>
              <span
                onClick={ProPasswordToggler}
                className={`${ShowProPass ? "" : "hidepass"} showpass`}
              >
                <i
                  className={`fa-solid fa-eye${ShowProPass ? "" : "-slash"} `}
                ></i>
              </span>
            </div>
            <div class="input-box">
              <label for="set_pro_pass">New Password</label>
              <input
                ref={ReProPassFieldRef}
                onChange={HandleReProPassField}
                type={ShowReProPass ? "text" : "password"}
                id="set_pro_pass"
              />
              <p ref={NewReProPassErrRef}>
                Change the password but create a strong one.
              </p>
              <span
                onClick={ReProPasswordToggler}
                className={`${ShowReProPass ? "" : "hidepass"} showpass`}
              >
                <i
                  className={`fa-solid fa-eye${ShowReProPass ? "" : "-slash"} `}
                ></i>
              </span>
            </div>

            <div ref={submitBtn} className="input-btn">
              <button type="submit" ref={ResetPassBtnRef}>
                Reset Password
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
