import React, { useEffect, useRef, useState } from 'react';

const FilterDropdown = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const [filteredList, setFilteredList] = useState(props.List);

  const dropdownRef = useRef(null);
  const searchRef = useRef(null);

  const handleDropdown = () => setIsOpen(!isOpen);

  const handleClickOutside = (e) => {
    if (!dropdownRef.current.contains(e.target) && !searchRef.current.contains(e.target)) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, []);

  const handleSearch = (e) => {
    const key = e.target.value.toLowerCase();
    const filteredList = props.List.filter(item => item.value.toLowerCase().includes(key));
    setFilteredList(filteredList);
  };

  return (
    <div className={`dropdown-wrapper dropdown-${props.Unique}`} ref={dropdownRef}>
      <button className={`dropdown-btn dropdown-btn-${props.Unique}`} onClick={handleDropdown}>
        <div><i className={`${props.btnIcon}`}></i> <span>{props.btnTitle}</span></div>
        <div><i className={`fa-solid fa-angle-down ${isOpen ? "rotate-90" : ""}`}></i></div>
      </button>
      <div className={`dropdown-list dropdown-list-${props.Unique} ${isOpen ? "active" : ""}`}>
        {props.Search && (
          <div className='dropdown-search' ref={searchRef}>
            <input type="text" onKeyUp={handleSearch} id='searchItems' placeholder='Search Tags' />
            <span><i className='fa-regular fa-magnifying-glass'></i></span>
          </div>
        )}

        <ul>
          {filteredList.map((item, i) => (
            <li key={i}>
              <input id={item.id} type={props.Unique !== "two" ? "checkbox" : "radio"} style={{ display: `${props.IsCheck ? "block" : "none"}` }} />
              <label htmlFor={item.id}>{item.value}</label>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

FilterDropdown.defaultProps = {
  List: [],
  Search: false,
  IsCheck: false,
  btnTitle: "Select",
  btnIcon: ""
};

export default FilterDropdown;
