import React, { useRef } from "react";
import plane from "../../images/plane.png";
export default function VerifyResetPassword(props) {
  const LoginEmailRef = useRef(null);
  const VerifyEmailForPassRef = useRef(null);

  async function HandleResetPass(e) {
    e.preventDefault();
    VerifyEmailForPassRef.current.innerHTML = "<i class='fa-light fa-sync fa-spin'></i>"
    await fetch(`http://localhost:8000/api/reset-password`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email: LoginEmailRef.current.value }),
    })
      .then((Response) => {
        return Response.json()
    }).then((Res)=>{
if(Res.errors){
  VerifyEmailForPassRef.current.innerHTML = "Send Request"
  props.ShowToast(
    "fa-xmark-circle ired",
    Res.errors,
    "Your entering email address doesn't registered here. "
  );
}else{
  VerifyEmailForPassRef.current.innerHTML = "Reset Link Sended <i class='fa-solid fa-check-circle'></i>"
  
  props.ShowToast(
        "fa-check-circle igreen",
        "Reset Link Sended",
        "Check your email address "
      );

}
      console.log(Res);

      })
      .catch((e)=>{
        console.log(e)
      })
     
  }

  return (
    <>
      <div className="reset-pass-wrapper">
        <div className="reset-pass-container">
          <div className="reset-pass-content">
            <div className="img">
              <img src={plane} loading="lazy" alt="" />
            </div>
            <h2 className="">Forget Your Password?</h2>
            <p>
              Please enter your email address below to recieve your user and a
              new password.
            </p>
          </div>
          <form method="post" onSubmit={HandleResetPass} className="input-box">
            <div>
              <label htmlFor="verify_email">Verify Email Address</label>
              <input
                ref={LoginEmailRef}
                type="text"
                id="verify_email"
                name="verify_email"
                required
              />
            </div>
            <div className="submit-btn">
              <button type="submit" ref={VerifyEmailForPassRef}>
                Send Request
              </button>
            </div>
          </form>
        </div>
      </div>
    </>
  );
}
