import React from "react";
import PortionEnd from "./PortionEnd";
import TestimonialCard from "./TestimonialCard";
export default function Testimonials() {
  return (
    <>

<SkeletonTheme
              baseColor={SkBgColor}
              highlightColor={SkhighlightColor}
            >
              <div className="card">
                <div className={`cat-icon`}>
                  <Skeleton width={50} borderRadius={10} height={50} />
                </div>
                <h3 className="cat-title">{<Skeleton width={"80%"} />}</h3>
                <small className="pcat-title">
                  {<Skeleton width={"60%"} />}
                </small>
                <p className="cat-desc">{<Skeleton count={2} />}</p>
              </div>
            </SkeletonTheme> 

      <div className="testimonial">
        <div className="testimonial-heading">
          <h1>Testmonials</h1>
          <p>"Cover what others are saying about us, Read our blowing testimonials"</p>
        </div>
        <div className="testimonial-container">
   <TestimonialCard/>
        </div>
      </div>
<PortionEnd title="Load more Reviews"/>
    </>
  );
}
