import React, { useEffect } from "react";
import BlogCard from "./BlogCard";
import PortionEnd from "../Assets/PortionEnd";
import PortionTop from "../Assets/PortionTop";

export default function BlogContainer() {
  const [Posts, setPosts] = useState("");
  const SkBgColor = getComputedStyle(document.body).getPropertyValue(
    "--skeleten-base-clr"
  );
  const SkhighlightColor = getComputedStyle(document.body).getPropertyValue(
    "--skeleten-highlight-clr"
  );

  const [IsLoading, setIsLoading] = useState(false);
  useLayoutEffect(() => {
    FetchCategory();
  }, []);
  async function FetchCategory() {
    setIsLoading(true);
    await fetch("http://localhost:8000/api/category", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((Response) => {
        return Response.json();
      })
      .then((Data) => {
        setIsLoading(false);
        setCategory(Data);
      })
      .catch((e) => {
        console.log(e);
      });
  }
  return (
    <>
      <div className="cards-container">
        <PortionTop title={"Latest Articles"} btn_title={"browse all articles"} btn_url={"/browse"} />
        <div className="post-grid">



          <BlogCard />
          <BlogCard />
          <BlogCard />
          <BlogCard />
          <BlogCard />
          <BlogCard />
          <BlogCard />
          <BlogCard />



        </div>
        <PortionEnd title="Load More Articles"/>
      </div>
    </>
  );
}
