import React from "react";
import $ from "jquery";

export default function AdminBanner() {
  $(document).ready(() => {
    let admins = $(".admin-cover-grid .admin-cover");
let centeradmin = Math.ceil(admins.length / 2);
admins.each((admin) => {
  console.log(admins[admin].findIndex())
  if(admins[admin] === centeradmin){
        admins[admin].addClass("active")
      }

    });
  });
  return (
    <>
      <div className="main-admin-intro-banner">
        <div className="m-a-i-b-wrapper">
          <div className="admin-cover-grid">
            <div className="admin-cover 1"></div>
            <div className="admin-cover 2"></div>
            <div className="admin-cover 3"></div>
            <div className="admin-cover 4"></div>
            <div className="admin-cover 5"></div>
            <div className="admin-cover 6"></div>
            <div className="admin-cover 7"></div>
            <div className="admin-cover "></div>
          </div>
          <div className="m-a-i-b-text">
            The best Professionate Website Developers & Designers are here for
            you
          </div>
        </div>
      </div>
    </>
  );
}
