import React from 'react'
import { useState } from 'react'

export default function Toast(props) {
  const [toastShow,settoastShow] = useState(false)
  function HideToast(){
settoastShow(false)
}

  return (
    <>
    {  props.toastData && <div className={`toast ${toastShow && "hide" }`}>
      <i className='fa-solid fa-info-circle'></i>
      <div className='toast-content'>
        <p>{props.title}</p>Information
        <small>{props.msg}</small>You can switch between artboards. You can view it down below
      </div>
      <div>
        <button onClick={HideToast}><i className='fa-solid fa-xmark'></i></button>
      </div>
    </div>
    }
    </>
  )
}
