import React, { useEffect, useState } from 'react'

export default function FilterDropdown(props) {
  const [isOpen, setIsOpen] = useState(false)

  function handleDropdown() {
    setIsOpen(!isOpen)
  }

  useEffect(() => {
    document.addEventListener("click", handleClickOutside);
    return () => document.removeEventListener("click", handleClickOutside);
  }, [])

  function handleClickOutside(e) {
    const dropdownBtn = document.querySelector(`.dropdown-btn-${props.btnTitle}`);
    const dropdownList = document.querySelector(`.dropdown-list-${props.btnTitle}`);
    if (dropdownBtn && dropdownList && !dropdownBtn.contains(e.target) && !dropdownList.contains(e.target)) {
      setIsOpen(false);
    }
  }

  return (
    <div className={`dropdown-wrapper dropdown-${props.btnTitle}`}>
      <button className={`dropdown-btn dropdown-btn-${props.btnTitle}`} onClick={handleDropdown}>
        <div><i className='fa-regular fa-tag'></i> <span>{props.btnTitle}</span></div>
        <div><i className={`fa-solid fa-angle-down ${isOpen ? "rotate-90" : ""}`}></i></div>
      </button>
      <div className={`dropdown-list dropdown-list-${props.btnTitle} ${isOpen ? "active" : ""}`}>
        <div className='dropdown-search'>
          <input type="text" placeholder='Search Tags'/>
          <span><i className='fa-regular fa-magnifying-glass'></i></span>
        </div>
        <ul>
          <li><input id='1' type="checkbox" /> <label htmlFor='1'>JavaScript</label></li>
          <li><input id='2' type="checkbox" /> <label htmlFor='2'>HTML</label></li>
          <li><input id='3' type="checkbox" /> <label htmlFor='3'>CSS</label></li>
          <li><input id='4' type="checkbox" /> <label htmlFor='4'>React</label></li>
        </ul>
      </div>
    </div>
  )
}
