import React, { useEffect } from 'react'
import DarkThemeImage from "../../images/DarkThemeIcon.png";
import LightThemeIcon from "../../images/LightThemeIcon.png";

// import DarkThemeImage from "../../../images/adminbanner.png"
export default function Appearance(props) {
    var getTheme = localStorage.getItem("theme_mode")
    useEffect(() => {
        //   document.addEventListener("click", handleClickOutside);
        document.body.classList.add(getTheme)
        SwitchTheme();
    }, [])
    const SwitchTheme = () => {
        const themes = document.querySelectorAll(".appearance_container button");
        themes.forEach((theme) => {
            theme.addEventListener('click', () => {
                const mode = theme.dataset.mode;
                document.body.classList.remove(mode === "dark" ? "light" : "dark");
                localStorage.setItem("theme_mode", mode);
                document.body.classList.add(mode);
                if (mode === "dark") {
                    props.ShowToast(
                        "fa-moon-cloud gray",
                        "Dark Mode Enabled",
                        "The dark mode has been successfully turned on."
                    )
                } else if (mode === "light") {
                    props.ShowToast(
                            "fa-sun orange",
                            "Light Mode Enabled",
                            "The light mode has been successfully turned on."
                        )
                    }
                })
            })
        }

    return (
        <div className='personal_pro_wrap'>
            <div className='personal_pro_head'>
                Choose your theme
            </div>
            <div className='appearance_container'>
                <div>
                    <button data-mode="dark" className='theme_box active'>
                        <img src={DarkThemeImage} alt="" />
                        <div className='select_theme_switch'>
                            <label htmlFor='select_theme'><i className='fa-solid fa-check'></i></label>
                            <input id='select_theme' type="radio" />
                        </div>
                    </button>
                    <div className='theme_box_title'>Nightfall</div>
                </div>
                <div>
                    <button data-mode="light" className='theme_box'>
                        <img src={LightThemeIcon} alt="" />
                        <div className='select_theme_switch'>
                            <label htmlFor='select_theme'><i className='fa-solid fa-check'></i></label>
                            <input id='select_theme' type="radio" />
                        </div>
                    </button>
                    <div className='theme_box_title'>Sunrise</div>
                </div>
            </div>
        </div >)
}
