import React, { useEffect, useState } from 'react'

export default function FilterDropdown(props) {
  const [isOpen, setIsOpen] = useState(false)

  function handleDropdown() {
    setIsOpen(!isOpen)
  }

  useEffect(() => {
    document.addEventListener("click", handleClickOutside);
  }, [])

  function handleClickOutside(e) {
    const dropdownBtn = document.querySelector(`.dropdown-btn-${props.Unique}`);
    const dropdownList = document.querySelector(`.dropdown-list-${props.Unique}`);
    if (!dropdownBtn.contains(e.target) && !dropdownList.contains(e.target)) {
      setIsOpen(false);
    }
  }
  const list = document.getElementById("list");

  const searchItems = (key) => {
    // const filteredList = props.List.filter(item => item.toLowerCase().indexOf(key) !== -1);
    // const itemsHTML = filteredList.map((item, i) => `<li key=${i}><input id=${item} type=${props.Unique !== "two" ? "checkbox" : "radio"} style="display: ${props.IsCheck ? "" : "none"}" /> <label htmlFor=${item}>${i + " " + item}</label></li>`).join('');
    // list.innerHTML = itemsHTML;
  }

  return (
    <div className={`dropdown-wrapper dropdown-${props.Unique}`}>
      <button className={`dropdown-btn dropdown-btn-${props.Unique}`} onClick={handleDropdown}>
        <div><i className={`${props.btnIcon}`}></i> <span>{props.btnTitle}</span></div>
        <div><i className={`fa-solid fa-angle-down ${isOpen ? "rotate-90" : ""}`}></i></div>
      </button>
      <div className={`dropdown-list dropdown-list-${props.Unique} ${isOpen ? "active" : ""}`}>
        {props.Search && <div className='dropdown-search'>
          <input type="text" onKeyUp={(e) => { return searchItems(e.target.value) }} id='searchItems' placeholder='Search Tags' />
          <span><i className='fa-regular fa-magnifying-glass'></i></span>
        </div>}

        <ul id='list'>
          {
            props.List.map((item) => {
              return  <li key={i}><input id={item.id} type={props.Unique !== "two" ? "checkbox" : "radio"} style={{ display: `${props.IsCheck ? "" : "none"}` }} /> <label htmlFor={item}>{item}</label></li>
            })
          }

        </ul>
      </div>
    </div>
  )
}
FilterDropdown.defaultProps = {
  List: [],
  Search: false,
  IsCheck: false,
  btnTitle: "Select",
  btnIcon: ""
}
